#!/bin/bash

# Get the result plots (i.e., the plots in the paper) from a running WARio docker container

# Get the image id (using docker ps) Make sure the container is running
# Overwrite this if you have multiple wario-experiments image instances running
image=$(docker ps | grep wario-experiments | awk '{ print $1 }')

# Create a results directory
mkdir -p generated

# Copy all the figures generated by the docker container
docker cp $image:/root/WARio/plotting/plots ./generated/

# Copy all the 'notebook' html files
docker cp $image:/root/WARio/plotting/notebooks ./generated/

# Copy all the 'raw' results used by the notebooks
docker cp $image:/root/WARio/benchmarks/results ./generated/
docker cp $image:/root/WARio/benchmarks/results-powertrace ./generated/
docker cp $image:/root/WARio/benchmarks/results-powertrace-tracefiles ./generated/
